    int dir[5] = {-1, 0, 1, 0, -1};
    bool isValid(int nx, int ny, int n, int m){
        if(nx < 0 || nx >= n || ny < 0 || ny >= m) return false;
        return true;
    }
    int bfs(pair<int, int> s, pair<int,int> e, vector<vector<int>>& forest){
        int n = forest.size(), m = forest[0].size();
        priority_queue<pii, vector<pii>, greater<pii>> pq; // {dist, {x, y}}
        vector<vector<int>> dis(n, vector<int>(m, INT_MAX));

        pq.push({0,{s.first, s.second}});
        dis[s.first][s.second] = 0;

        while(!pq.empty()){
            auto curr = pq.top(); pq.pop();
            int dist = curr.first, x = curr.second.first, y = curr.second.second;    
            if(make_pair(x, y) == e) return dis[x][y];

            for(int i = 0; i < 4; i++){
                int nx =  x+ dir[i], ny = y + dir[i+1];

                if(isValid(nx, ny, n, m) && dist+1 < dis[nx][ny] && forest[nx][ny] > 0){            
                    pq.push({dist+1, {nx, ny}});
                    dis[nx][ny] = 1 + dist;   
                }
            }
        }

        return -1;
    }

