        vector<pii> trees;
        trees.push_back({0,{0,0}});

        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                int h = forest[i][j];

                if(i==0 and j==0){
                    if(h>1) trees.push_back({h, {i, j}});
                }else if(h>1){
                    trees.push_back({h, {i, j}});
                }
            }
        }

        int res=0;
        sort(trees.begin(), trees.end());

        for(int i = 0; i < trees.size()-1; i++){
            auto s = trees[i].second;
            auto e = trees[i+1].second;

            int dist = bfs(s, e, forest);
            if(dist == -1){
                return -1;
            }else{
                res += dist;
            }
        }
        return res;
    }
