class Solution {
public:
    int x[4]={0,0,1,-1};
    int y[4]={1,-1,0,0};

    int bfs(vector<vector<int>> &forest,int curri,int currj,int dsti,int dstj,int m,int n){
        int out=0;
        queue<pair<int,int>> q;
        vector<vector<bool>> vis(m,vector<bool>(n,false));
        q.push({curri,currj});
        vis[curri][currj]=true;
        int steps=0;

        while(!q.empty()){
            steps++;
            int size=q.size();
            while(size--){
                int i=q.front().first;
                int j=q.front().second;
                q.pop();
                if(i==dsti && j==dstj)return steps-1;
                for(int k=0;k<4;k++){
                    int nr=i+x[k];
                    int nc=j+y[k];
                    if(nr>=0 && nr<m && nc>=0 && nc<n && !vis[nr][nc] && forest[nr][nc]!=0){
                        if(nr==dsti && nc==dstj)return steps;
                        q.push({nr,nc});
                        vis[nr][nc]=true;
                    }
                }
            }
        }

        return -1;
    }

    int cutOffTree(vector<vector<int>>& forest) {
        int m=forest.size();
        int n=forest[0].size();
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<pair<int,pair<int,int>>>> pq;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(forest[i][j]>1)pq.push({forest[i][j],{i,j}});
            }
        }
        pq.push({1,{0,0}});
        int out=0;
        while(pq.size()>1){
            int curri=pq.top().second.first , currj=pq.top().second.second;
            pq.pop();
            int dsti=pq.top().second.first , dstj=pq.top().second.second;
            int temp=bfs(forest,curri,currj,dsti,dstj,m,n);
            if(temp==-1)return -1;
            out+=temp;
        }
        return out;
    }
};
