class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        if forest[0][0] == 0 :
            return -1
        m = len(forest)
        n = len(forest[0])
        def distance(node1, node2):
            now = [node1]
            soon = []
            expanded = set()
            manhattan = abs(node1[0] - node2[0]) + abs(node1[1] - node2[1])
            detours = 0
            while True:
                if len(now) == 0:
                    now = soon
                    soon = []
                    detours += 1
                node = now.pop()
                if node == node2:
                    return manhattan + 2 * detours
                if node not in expanded:
                    expanded.add(node)
                    x, y = node
                    if x - 1 >= 0 and forest[x - 1][y] >= 1:
                        if x > node2[0]:
                            now.append((x - 1, y))
                        else:
                            soon.append((x - 1, y))
                    if y + 1 < n and forest[x][y + 1] >= 1:
                        if y < node2[1]:
                            now.append((x, y + 1))
                        else:
                            soon.append((x, y + 1))
                    if x + 1 < m and forest[x + 1][y] >= 1:
                        if x < node2[0]:
                            now.append((x + 1, y))
                        else:
                            soon.append((x + 1, y))
                    if y - 1 >= 0 and forest[x][y - 1] >= 1:
                        if y > node2[1]:
                            now.append((x, y - 1))
                        else:
                            soon.append((x, y - 1))
        trees = []
        for i in range(m):
            for j in range(n):
                if forest[i][j] > 1:
                    trees.append(((i, j), forest[i][j]))
        trees.sort(key=lambda x: x[1])
        can_reach = {(0, 0)}
        stack = [(0, 0)]
        while len(stack) > 0:
            x, y = stack.pop()
            if x - 1 >= 0 and forest[x - 1][y] >= 1 and (x - 1, y) not in can_reach:
                can_reach.add((x - 1, y))
                stack.append((x - 1, y))
            if y + 1 < n and forest[x][y + 1] >= 1 and (x, y + 1) not in can_reach:
                can_reach.add((x, y + 1))
                stack.append((x, y + 1))
            if x + 1 < m and forest[x + 1][y] >= 1 and (x + 1, y) not in can_reach:
                can_reach.add((x + 1, y))
                stack.append((x + 1, y))
            if y - 1 >= 0  and forest[x][y - 1] >= 1 and (x, y - 1) not in can_reach:
                can_reach.add((x, y - 1))
                stack.append((x, y - 1))
        for t in trees:
            if t[0] not in can_reach:
                return -1
        start = (0, 0)
        num_step = 0
        for t in trees: 
            num_step += distance(start, t[0])
            forest[t[0][0]][t[0][1]] = 1
            start = t[0]
        return num_step
