# Approach
1. Listing down the order of visiting cells.
2. Move from one cell to another based on order derieved in step 1.
    a. Use BFS to find minimum distance between two consecutive cells.

# Complexity
- Time complexity:
$$O( rows^2 * columns^2)$$

- Space complexity:
$$O(rows * columns)$$

# Code
```
class Solution {
    vector<vector<int>> gforest;
    pair<int,int> move[4] = {
        make_pair(0,1),
        make_pair(1,0),
        make_pair(0,-1),
        make_pair(-1,0)
    };

public:
    int bfs(pair<int,int> s, pair<int,int>des) {
        int mark[51][51];
        for (int i = 0 ; i < gforest.size(); i ++) {
            for (int j = 0 ; j < gforest[i].size() ; j ++) {
                mark[i][j] = 0;
            }
        }
        queue<pair<int,int> > q;
        q.push(s);

        mark[s.first][s.second] = 1;

        while(!q.empty()) {
            pair<int,int> nw = q.front();
            q.pop();
            if (nw == des) {
                return  mark[nw.first][nw.second] - 1;
            }

            for(int i = 0 ; i < 4; i ++) {
                pair<int,int>nxt = make_pair(nw.first+move[i].first,nw.second+move[i].second);

                if (nxt.first < 0 || nxt.second < 0 || nxt.first >= gforest.size() || nxt.second >= gforest[0].size() || mark[nxt.first][nxt.second] || gforest[nxt.first][nxt.second] == 0) {
                    continue;
                }

                mark[nxt.first][nxt.second] = 1 + mark[nw.first][nw.second];
                q.push(nxt);
            }

        }

        return -1;
    }

    int cutOffTree(vector<vector<int>>& forest) {
        gforest = forest;

        vector<pair<int, pair<int,int> > > order;
        for(int i = 0 ; i < forest.size() ; i ++ ) {
            for (int j = 0 ; j < forest[i].size(); j ++) {
                if (forest[i][j] <= 1 ) continue;
                order.push_back(make_pair(forest[i][j], make_pair(i,j)));
            }
        }

        int cost = 0;
        sort(order.begin(), order.end());
        if (order[0].second != make_pair(0,0)) {
            cost = bfs(make_pair(0,0), order[0].second);
            if (cost == - 1) {
                return -1;
            }
        }

        for (int i = 1 ; i < order.size() ; i ++) {
            int dis = bfs(order[i-1].second, order[i].second);
            if (dis == -1) {
                return -1;
            }
            cost += dis;
        }

        return cost;
    }
};
