class Solution {

    int cur , n;
    public int cutOffTree(List<List<Integer>> forest) {
        if (forest.get(0).get(0) == 0) return -1;
        n = forest.get(0).size();
        cur = 0;

        PriorityQueue<Integer> pq = new PriorityQueue<>((a,b) -> (Integer.compare(forest.get(a/n).get(a%n), forest.get(b/n).get(b%n))));
        for (int i = 0; i < forest.size() ; i++)
            for (int j = 0; j < forest.get(0).size(); j++) 
                if (forest.get(i).get(j) > 1) pq.offer(i*n + j);
        int ans = 0;
        final int [] dirs = new int [] {n,-n,1,-1};
        while(!pq.isEmpty() && cur > -1) ans += bfs (pq.poll(), forest, dirs);
        return cur == -1 ? -1 : ans;
    }

    private int bfs(int to, List<List<Integer>> forest , int [] dirs){
        if (cur == to) return 0;
        int steps = 0;
        Set<Integer> visited = new HashSet();
        Queue<Integer> q = new LinkedList();
        q.offer(cur);
        visited.add(cur);
        while(!q.isEmpty()){
            steps++;
            int size = q.size();
            for (int i= 0; i < size; i++){
                int from = q.poll(); 
                for (int dir: dirs){
                    if (n!=1 && ((dir == 1 && from % n == n-1)  || (dir == -1 && from % n == 0))) continue; // cant jump to next row first cell from last cell
                    int next = from + dir;
                    if (next < 0 || next >= forest.size() * n || forest.get(next/n).get(next%n) == 0 || !visited.add(next)) continue;
                    if (next == to) {
                        forest.get(next/n).set(next%n, 1) ;
                        cur = to;
                        return steps;
                    }
                    q.offer(next);
                }
            }
        }     
        cur = -1;
        return -1;
    }
}
