from typing import List
from collections import deque

class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        m, n = len(forest), len(forest[0])

        # Step 1: Extract and sort tree positions
        trees = []
        for i in range(m):
            for j in range(n):
                if forest[i][j] > 1:
                    trees.append((forest[i][j], i, j))
        trees.sort()

        # Step 2: Helper function for BFS
        def bfs(start, target):
            queue = deque([(start[0], start[1], 0)])
            visited = set([(start[0], start[1])])

            while queue:
                x, y, steps = queue.popleft()
                if x == target[0] and y == target[1]:
                    return steps

                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    nx, ny = x + dx, y + dy
                    if 0 <= nx < m and 0 <= ny < n and forest[nx][ny] != 0 and (nx, ny) not in visited:
                        queue.append((nx, ny, steps + 1))
                        visited.add((nx, ny))

            return -1

        # Step 3 and 4: Iterate over trees and calculate steps
        steps = 0
        cur_pos = (0, 0)
        for _, target_x, target_y in trees:
            cur_steps = bfs(cur_pos, (target_x, target_y))
            if cur_steps == -1:
                return -1
            steps += cur_steps
            cur_pos = (target_x, target_y)

        return steps

