                        queue.append((nx, ny, steps + 1))
                        visited.add((nx, ny))

            return -1

        # Step 3 and 4: Iterate over trees and calculate steps
        steps = 0
        cur_pos = (0, 0)
        for _, target_x, target_y in trees:
            cur_steps = bfs(cur_pos, (target_x, target_y))
            if cur_steps == -1:
                return -1
            steps += cur_steps
            cur_pos = (target_x, target_y)

        return steps

