    def bfs(self, forest, begin, target) -> int:
        if begin == target:
            return 0
        q = Queue()
        steps = 0
        q.put((begin, 0))
        visited = [[False for j in range(len(forest[0]))] for i in range(len(forest))]
        visited[begin[0]][begin[1]] = True
        while q.qsize() > 0:
            at = q.get()
            c = at[0]
            dist = at[1]
            row = c[0]
            col = c[1]
            visited[row][col] = True
            if row == target[0] and col == target[1]:
                return dist

            if row < len(forest) - 1 and forest[row+1][col] != 0 and not visited[row+1][col]:
                q.put(((row+1, col), dist+1))
            if row > 0 and forest[row-1][col] != 0 and not visited[row-1][col]:
                q.put(((row-1, col), dist+1))
            if col < len(forest[0]) - 1 and forest[row][col + 1] != 0 and not visited[row][col + 1]:
                q.put(((row, col+1), dist+1))
            if col > 0 and forest[row][col - 1] != 0 and not visited[row][col - 1]:
