class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        if forest[0][0] == 0:
            return -1

        M = len(forest)
        N = len(forest[0])

        storage = {} # height to coords
        heights = [] # heights sorted  

        for i in range(M):
            for j in range(N):
                if forest[i][j] > 1:
                    storage[forest[i][j]] = (i, j)
                    heights.append(forest[i][j])
        heights.sort()

        # bfs
        def steps(start, finish):
            visited = set()
            n_steps = 0
            start_i, start_j = start
            finish_i, finish_j = finish
            forest[start_i][start_j] = 1
            q = collections.deque([start])
            visited.add(start)
            while q:
                next_level = []
                for i in range(len(q)):
                    cur_i, cur_j = q.popleft()
                    poss_arr = [(cur_i, cur_j + 1), (cur_i, cur_j - 1), (cur_i - 1, cur_j), (cur_i + 1, cur_j)]
                    for poss_dest_i, poss_dest_j in poss_arr:
                        if poss_dest_i < 0 or poss_dest_i >= M or poss_dest_j < 0 or poss_dest_j >= N:
                            continue
                        if (poss_dest_i, poss_dest_j) in visited:
                            continue
                        if forest[poss_dest_i][poss_dest_j] == 0:
                            continue
                        if (poss_dest_i, poss_dest_j) == finish:
                            return n_steps + 1
                        visited.add((poss_dest_i, poss_dest_j))
                        next_level.append((poss_dest_i, poss_dest_j))
                q.extend(next_level)
                n_steps += 1
            return -1

        total_res = 0        
        cur_coords = (0, 0)
        for tree in heights:
            if storage[tree] == cur_coords: # if starting on lowest tree
                forest[cur_coords[0]][cur_coords[1]] = 1
                continue
            else:
                cur_steps = steps(cur_coords, storage[tree])
                if cur_steps == -1:
                    return -1
                else:
                    total_res += cur_steps
                    cur_coords = storage[tree]

        return total_res
