class Solution {

    int[][] dirs = new int[][] {{0,-1}, {-1,0}, {1,0}, {0, 1}};
    public int cutOffTree(List<List<Integer>> forest) {
        //Note: The input is generated such that no two trees have the same height, 
        //and there is at least one tree needs to be cut off.
        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b) -> (a[0] - b[0]));

        addToQueue(forest, pq);
        int ans = 0;
        int[] src = new int[]{0,0};
        while(!pq.isEmpty()){
            int[] pos = pq.poll();
            int[] dest = new int[]{pos[1], pos[2]};
            int steps = bfs(src, dest, forest);
            if (steps == -1)
                return -1;
            ans += steps;
            src = dest;
        }
        return ans;
    }


    private int bfs(int[] src, int[] dest, List<List<Integer>> forest){

        Deque<int[]> queue = new LinkedList<>();
        int m = forest.size();
        int n = forest.get(0).size();
        boolean[][] visited = new boolean[m][n];
        int sr = src[0], sc = src[1];
        int dr = dest[0], dc = dest[1];

        int d = 0;
        queue.offer(new int[]{sr, sc, d});

        while(!queue.isEmpty()){

            int size = queue.size();
            for (int i = 0; i < size; i++){
                int[] tree = queue.poll();
                int cr = tree[0], cc = tree[1];
                if (cr == dr && cc == dc){
                    System.out.println( "sr:" + sr + " sc:" + sc + " dr:" + dr + " dc:" + " d:" + tree[2]);
                    return tree[2];
                }
                visited[cr][cc] = true;
                for (int[] neigh : getNeighbors(tree, m, n)){
                    int nr = neigh[0],  nc = neigh[1];
                    if (!visited[nr][nc] && forest.get(nr).get(nc) != 0){
                        queue.offer (new int[] { nr, nc, d + 1});
                        visited[nr][nc] = true;
                    }
                }
            }
            d++;
        }
        return -1;
    }



    private List<int[]> getNeighbors(int[] src, int m, int n){

        List<int[]> result = new ArrayList<>();
        int r = src[0], c = src[1];
        for (int[] dir : dirs){
            int nr = r + dir[0], nc = c + dir[1];
            if (nr >= 0 && nr < m && nc >= 0 && nc < n){
                result.add(new int[] { nr, nc});
            }
        }
        return result;
    }

    private void addToQueue(List<List<Integer>> forest, PriorityQueue<int[]> pq ){
        int m = forest.size();
        int n = forest.get(0).size();

        for (int i = 0; i < m; i++){
            for (int j = 0; j < n; j++){
                int height = forest.get(i).get(j);
                if (height > 1)
                    pq.offer(new int[]{height, i , j});
            }
        }
    }
}
