class Node {
public:
    Node(int _h, int _x, int _y) : height(_h), x(_x), y(_y) { }
    int height;
    int x;
    int y;
};

class Solution {
public:
    int cutOffTree(vector<vector<int>>& forest) {
        int steps = 0;
        if (forest.empty() || forest[0].empty()) {
            return -1;
        }
        auto comparator = [] (const Node& a, const Node& b) {
            return a.height > b.height;
        };
        priority_queue<Node, vector<Node>, decltype(comparator)> store{comparator};
        for (int i = 0; i < forest.size(); ++i) {
            for (int j = 0; j < forest[0].size(); ++j) {
                int height = forest[i][j];
                if (height > 1) {
                    store.emplace(height, i, j);
                }
            }
        }

        Node current = {forest[0][0], 0, 0};
        while (!store.empty()) {
            auto target = store.top();
            store.pop();
            int depth = distance(forest, {current.x, current.y}, {target.x, target.y});
            if (depth < 0) {
                return -1;
            }
            steps += depth;
            current = target;
        }
        return steps;
    }

private:
    int distance(const vector<vector<int>>& forest,
                 const std::pair<int, int>& current,
                 const std::pair<int, int>& target) {
        int depth = 0;
        if (current == target) {
            return depth;
        }
        vector<vector<int>> dirs{{0, -1}, {-1, 0}, {0, 1}, {1, 0}};
        queue<pair<int, int>> store;
        store.push(current);
        vector<vector<bool>> marked(forest.size(), vector<bool>(forest[0].size(), false));
        marked[current.first][current.second] = true;

        while (!store.empty()) {
            ++ depth;
            int size = store.size();
            for (int i = 0; i < size; ++i) {
                auto pos = store.front();
                store.pop();
                for (int k = 0; k < dirs.size(); ++ k) {
                    int ni = pos.first + dirs[k][0];
                    int nj = pos.second + dirs[k][1];
                    if (ni >= forest.size() || nj >= forest[0].size() || ni < 0 || nj < 0) {
                        continue;
                    }
                    if (ni == target.first && nj == target.second) {
                        return depth;
                    }
                    if (!marked[ni][nj] && (forest[ni][nj] >= 1)) {
                        store.emplace(ni, nj);
                        marked[ni][nj] = true;
                    }
                }
            }
        }
        return -1;
    }
};
