class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        n, m = len(forest), len(forest[0])
        order = []

        for i1, j1 in enumerate(forest):
            for i2, j2 in enumerate(j1):
                if j2 > 1: 
                    order.append([i1, i2, j2])

        order.sort(key = lambda x : x[2])

        res = 0
        q = [(0, 0, 0)]
        for i in order:
            visit = [[False] * m for _ in range(n)]
            found = False
            while q:
                x, y, level = q.pop()
                if not (0 <= x < n and 0 <= y < m) or visit[x][y] or forest[x][y] == 0:
                    continue
                if (x, y) == (i[0], i[1]):
                    res += level
                    found = True
                    break
                visit[x][y] = True
                q.insert(0, (x+1, y, level+1))
                q.insert(0, (x-1, y, level+1))
                q.insert(0, (x, y+1, level+1))
                q.insert(0, (x, y-1, level+1))
            if not found:
                return -1
            q = [(i[0], i[1], 0)]
        return res

