import heapq

class Solution:
    def cutOffTree(self, forest: List[List[int]]) -> int:
        """
        push all trees into a heap
        iterate over heap and compute the min distance b/w each tree
        add those steps to current steps
        """
        m, n = len(forest), len(forest[0])
        trees = []
        total_steps = 0
        start_x, start_y = 0, 0
        for i in range(m):
            for j in range(n):
                if forest[i][j] > 1:
                    heapq.heappush(trees, (forest[i][j], i, j))

        def compute_distance_bw_tree(sx, sy, ex, ey):
            steps = 0
            directions = [(1, 0), (-1, 0), (0, 1), (0, -1)]
            que = deque([(sx, sy)])
            visited = {(sx,sy)}
            while len(que):
                for _ in range(len(que)):
                    i, j = que.popleft()
                    if (i,j) == (ex, ey):
                        return steps
                    for dx, dy in directions:
                        x = i + dx
                        y = j + dy
                        if 0 <= x < m and 0 <= y < n and forest[x][y] != 0 and (x,y) not in visited:
                            que.append((x,y))
                            visited.add((x,y))
                steps+=1
            return -1

        while len(trees):
            height, end_x, end_y = heapq.heappop(trees)
            steps = compute_distance_bw_tree(start_x, start_y, end_x, end_y)
            if steps == -1:
                return -1
            total_steps+=steps
            start_x, start_y = end_x, end_y
        return total_steps    
