# Code
```
class Solution {

    List<List<Integer>> forest;
    List<Integer> treeHeights = new ArrayList<>();
    HashMap<Integer, List<Integer>> neighbours = new HashMap<>();
    int[] que;
    HashSet<Integer> visited = new HashSet<>();

    public int cutOffTree(List<List<Integer>> forest) {
        if (forest.get(0).get(0) == 0)
            return -1;

        this.forest = forest;
        this.que = new int[forest.size() * forest.get(0).size()];

        int height;

        for (int y = forest.size(); y-- > 0; ) {
            for (int x = forest.get(0).size(); x-- > 0; ) {
                height = forest.get(y).get(x);
                if (height == 0)
                    continue;
                if (height == 1)
                    height = getEmptyHeight(x, y);
                else
                    treeHeights.add(height);

                addNeighbours(x, y, height);
            }
        }

        Collections.sort(treeHeights);

        int pathLength = 0;
        height = forest.get(0).get(0);
        if (height == 1)
            height = getEmptyHeight(0, 0);
        int idx = 0;
        if (treeHeights.get(0) == height) {
            idx = 1;
        }
        int nextHeight;
        int heightCount = treeHeights.size();

        for (; idx < heightCount; idx++) {
            nextHeight = treeHeights.get(idx);
            try {
                pathLength += getDistance(height, nextHeight);
            } catch (Exception e) {
                return -1;
            }
            height = nextHeight;
        }

        return pathLength;
    }

    //for empty spaces, we assign a fake unique height to track it as a unique neighbor
    private int getEmptyHeight(int x, int y) {
        return - 100 - x * 100 - y;
    }

    private int getDistance(int fromHeight, int toHeight) throws Exception {
        int quePos = 0;
        que[quePos] = fromHeight;
        int queSize = 1;

        visited.clear();
        visited.add(fromHeight);

        for (int len = 1; quePos < queSize; len++) {
            for (int queCurrentLen = queSize - quePos; queCurrentLen-- > 0; ) {
                for (Integer neighborHeight: neighbours.get(que[quePos++])) {
                    if (neighborHeight == toHeight)
                        return len;
                    if (visited.contains(neighborHeight))
                        continue;
                    que[queSize++] = neighborHeight;
                    visited.add(neighborHeight);
                }
            }
        }

        throw new Exception();
    }

    private void addNeighbor(int from, int to, int len) {
        List<Integer> neighbourList = neighbours.get(from);
        if (neighbourList == null) {
            neighbourList = new ArrayList<Integer>();
            neighbours.put(from, neighbourList);
        }
        neighbourList.add(to);
    }

    private void addNeighbours(int x, int y, int from) {
        int h;

        if (x > 0) {
            h = getCellHeight(x - 1, y);
            if (h != 0)
                addNeighbor(from, h, 1);
        }

        if (x < forest.get(0).size() - 1) {
            h = getCellHeight(x + 1, y);
            if (h != 0)
                addNeighbor(from, h, 1);
        }

        if (y > 0) {
            h = getCellHeight(x, y - 1);
            if (h != 0)
                addNeighbor(from, h, 1);
        }

        if (y < forest.size() - 1) {
            h = getCellHeight(x, y + 1);
            if (h != 0)
                addNeighbor(from, h, 1);
        }
    }

    private int getCellHeight(int x, int y) {
        int h = forest.get(y).get(x);
        if (h == 1)
            return getEmptyHeight(x, y);
        else
            return h;
    }
}
