class Solution:
    def cutOffTree(self, f):
        m, n, result = len(f), len(f[0]), []

        for i in range(m):
            for j in range(n):
                if f[i][j] > 1:
                    heapq.heappush(result,(f[i][j],i,j))

        total, start_pos = 0, (0,0)

        def bfs(start,end):
            stack, visited = [(start[0],start[1],0)], set()

            visited.add((start[0],start[1]))

            while stack:
                i, j, steps = stack.pop(0)

                if (i,j) == end:
                    return steps

                for ni,nj in [(i-1,j),(i+1,j),(i,j-1),(i,j+1)]:
                    if 0 <= ni < m and 0 <= nj < n and (ni,nj) not in visited and f[ni][nj] != 0:
                        stack.append((ni,nj,steps+1))
                        visited.add((ni,nj))

            return -1

        while result:
            val, x, y = heapq.heappop(result)
            res = bfs(start_pos,(x,y))
            if res >= 0:
                total += res
                start_pos = (x,y)
            else:
                return -1

        return total












