class Solution {
    private int[][] neighbors = {{0,1}, {1,0}, {0,-1}, {-1,0}}; 
    private class Pair{
        private int x, y, h;
        public Pair(int _x, int _y, int _h){
            x = _x;
            y = _y;
            h = _h;
        }
    }
    private int rows, cols;
    public int cutOffTree(List<List<Integer>> forest) {
        rows = forest.size(); cols = forest.get(0).size();
        int h;
        List<Pair> heights = new ArrayList<>();
        for(int row = 0 ; row < rows ; ++row){
            for(int col = 0; col < cols; ++col){
                h = forest.get(row).get(col);
                if(h > 1){
                    heights.add(new Pair(row, col, h));
                }
            }
        }
        Collections.sort(heights, (p1,p2)->p1.h - p2.h);
        int totalDistance = 0, sx = 0, sy = 0, tx, ty, d;
        for(int i = 0; i<heights.size(); ++i){
            d = findDistanceBtwTrees(forest, sx, sy, heights.get(i).x, heights.get(i).y);
            if(d == -1) return -1;
            totalDistance += d;
            sx = heights.get(i).x; sy = heights.get(i).y;
        }
        return totalDistance;
    }

    private int findDistanceBtwTrees(List<List<Integer>> forest, int sx, int sy, int tx, int ty){
        LinkedList<Pair> queue = new LinkedList<>();
        boolean[][] visited = new boolean[rows][cols];
        queue.offer(new Pair(sx, sy, 0));
        visited[sx][sy] = true;
        int r, c;
        while(!queue.isEmpty()){
            Pair cell = queue.pollFirst();
            if(cell.x == tx && cell.y == ty) return cell.h;
            for(int[] d : neighbors){
                r = cell.x + d[0]; c = cell.y + d[1];
                if(r >= 0 && r < rows && c >= 0 && c < cols && !visited[r][c] && forest.get(r).get(c) >= 1){
                    visited[r][c] = true;
                    queue.offer(new Pair(r, c, cell.h + 1));
                }
            }
        }
        return -1;
    }
}
