            if (s.charAt(i) == '(') right--;
            else if (s.charAt(i) == ')') right++;
            else rightx++;
            if (right < 0){
                if (rightx == 0) return false;
                else{
                    rightx--;
                    right++;
                }
            }
        }
        return true;
    }
}
