    public boolean checkFromLeft(String s) {
        if (s == null || s.length() == 0) {
            return true;
        }
        int star = 0;
        int open = 0;
        int close = 0;
        for (char c : s.toCharArray()) {
            if (c == '(') {
                open++;
            } else if (c == ')') {
                close++;
            } else {
                star++;
            }
            if (close > open + star) {
                return false;
            }
        }
        if (close == open || close - open <= star) {
            return true;
        }
        return false;
    }
    public boolean checkFromRight(String s) {
        if (s == null || s.length() == 0) {
            return true;
        }
        int star = 0;
        int open = 0;
        int close = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            char c = s.charAt(i);
            if (c == ')') {
                open++;
            } else if (c == '(') {
                close++;
            } else {
                star++;
            }
            if (close > open + star) {
                return false;
            }
        }
        if (close == open || close - open <= star) {
            return true;
        }
        return false;
    }
    public boolean checkValidString(String s) {
        return checkFromLeft(s) && checkFromRight(s);
    }
