class Solution {
public:
    bool checkValidString(string s) {
        stack <int> brac, star;          // store opening bracket and star indices in spearate stacks
        int n = s.size();

        for (int i = 0; i < n; i++){
            if (s[i] == '(')            // push index in bracket stack if opening bracket occurs
                brac.push(i);

            else if (s[i] == '*')      // push index in star stack if asterisk occurs
                star.push(i);

            else {       // if closing bracket occurs
                if (!brac.empty())                  // validate it via an opening bracket 
                    brac.pop();                    // remove opening bracket's index as it has formed a pair with a closing bracket

                else if (!star.empty())          // if opening brackets are not present, validate it via an asterisk [Convert asterisk to opening bracket]
                    star.pop();                 // remove opening bracket's index as it has formed a pair with a closing bracket

                else 
                    return false;             // if neither an opening bracket nor an asterisk is present before a closing bracket, string is invalid
            }
        }

        // now all that's left is opening brackets and/or asterisks
		// if no opening brackets are left ie brac stack is empty, then all asterisks can be replaced via "" and hence our code will return true

        while (!brac.empty()){                                     // but, if opening brackets are still left, asterisk has to be converted to closing bracket to validate each of them
            if (star.empty() || brac.top() > star.top())          // if no asterisks or the asterisk occurs BEFORE the opening bracket[Opening bracket desperately needs a closing brackets AFTER it], we can't validate our string
                return false;
            else{ // remove from both as the pair has been validated
                brac.pop();
                star.pop();
            }
        }
        return true;        // aah, lovely! our brac stack is now empty which means we have validated the whole string pair by pair and it doesn't matter if our star stack is empty or not since all asterisks can now be considered as ""
    }
};
