            elif star_stack[-1] > left_par_stack[-1]: # Only when the idx of "*" is greater than idx of "(" that can we apply "*" as ")"
                star_stack.pop()
                left_par_stack.pop()
            elif star_stack[-1] < left_par_stack[-1]:
                break

        return not left_par_stack

