        int rightBalance = 0;
        for (int i = N - 1; i >= 0; i--) {
            char ch = s.charAt(i);
            if (ch == ')' || ch == '*') {
                rightBalance++;
            } else {
                rightBalance--;
            }
            if (rightBalance < 0) return false;  // not enough right parenthesis
        }
        return true;
    }
}
