class Solution {
public:
    int dp[101][101];
    bool checkValidString(string s) {
        memset(dp, -1, sizeof(dp));
        return find(s, 0, 0);
    }
    bool find(string s, int pos, int open) {
         if(pos == s.length() && !open) {
            return true;
        }
        if(pos>s.length() || open < 0) {
            return false;
        }
        if(dp[pos][open] != -1) {
            return dp[pos][open];
        }
        if(s[pos] == '(') {
            dp[pos][open] = find(s, pos+1, open+1);
        } else if(s[pos] == ')') {
            dp[pos][open] =  find(s, pos+1, open-1);
        } else {
            dp[pos][open] = find(s,pos+1, open+1) || find(s, pos+1, open-1) || find(s, pos+1, open);
        }
        return dp[pos][open];
    }

};
