    public boolean checkValidString(String s) {
        // 1st pass : treat all * as left (
        int leftWithWild = 0;
        for(char c : s.toCharArray()) {
            if(c == ')') {
                leftWithWild--;
                if(leftWithWild < 0) {
                    return false;
                }
            } else {
                leftWithWild++;
            }
        }

        // 2nd pass : treat all * as right )
        int rightWithWild = 0;
        for(int i = s.length() - 1; i >= 0; i--) {
            char c = s.charAt(i);
            if(c == '(') {
                rightWithWild--;
                if(rightWithWild < 0) {
                    return false;
                }
            } else {
                rightWithWild++;
            }
        } 
		// treat some of * as '(' and some of * as ')' 
        return true; 
    }
}
