class Solution {
    public boolean checkValidString(String s) {
        int countStar = 0;
		int countOpen = 0;
        int countClose = 0;
        char[] arr = s.toCharArray();

        // check left to right if string is invalid
        for (int i = 0; i < arr.length; i++) {
            if (arr[i] == '(') {
                countOpen++;
            } else if (arr[i] == ')') {
                if (countOpen > 0) {
                    countOpen--; // an existing open bracket can complement this closing bracket
                } else if (countStar > 0) {
                    countStar--; // an existing star can complement this closing bracket
                } else {
                    return false;
                }
            } else if (arr[i] == '*') {
                countStar++;
            }
        }

        countStar = 0;

        // check right to left if string is invalid
        for (int i = arr.length-1; i >= 0; i--) {
            if (arr[i] == ')') {
                countClose++;
            } else if (arr[i] == '(') {
                if (countClose > 0) {
                    countClose--; // an existing closing bracket can complement this opening bracket
                } else if (countStar > 0) {
                    countStar--; // an existing star can complement this opening bracket
                } else {
                    return false;
                }
            } else if (arr[i] == '*') {
                countStar++;
            }
        }

        return true;
    }
}
