class Solution {
public:
    bool checkValidString(string s) {
        int n = s.size();
        int stars = 0;
        int leftpar = 0, rightpar = 0;

        bool possible = true;
        for(int i = 0; i < n; ++i){
            if(s[i] == '('){
                ++leftpar;
            } else if(s[i] == ')')
                ++rightpar;
            else ++stars;

            if(rightpar > leftpar){
                int diff = rightpar - leftpar;
                if(diff > stars){
                    possible = false;
                    break;
