class Solution {
    public boolean checkValidString(String s) {
        Boolean[][] memo = new Boolean[s.length() + 1][s.length() + 1];
        return checkValidString(s, 0, 0, memo);
    }

    private boolean checkValidString(String s, int index, int count, Boolean[][] memo) {
        if (count < 0) {
            return false;
        }

        if (memo[index][count] != null) {
            return memo[index][count];
        }

        if (index == s.length()) {
            if (count == 0) {
                return true;
            } else {
                memo[index][count] = false;
                return false;
            }
        }

        if (s.charAt(index) == '(') {
            if (checkValidString(s, index + 1, count + 1, memo)) {
                return true;
            }
        } else if (s.charAt(index) == ')') {
            if (checkValidString(s, index + 1, count - 1, memo)) {
                return true;
            }
        } else {
            if (checkValidString(s, index + 1, count + 1, memo) ||
                checkValidString(s, index + 1, count - 1, memo) ||
                checkValidString(s, index + 1, count, memo)) {
                return true;
            }
        }

        memo[index][count] = false;
        return false;
    }
}
