        // if we have traversed the entire string, then at the end
        // the count of open parenthesis must be zero, it means all 
        // the open brackets are nullify with the closed brackets
        if(index == s.size()){
            return open == 0;
        }

        // if current character is '(' then increment the 
        // open parenthesis counter and move to the next index
        if(s[index] == '('){
            return solve(s, index + 1, open + 1);
        }
        // if current character is ')' then make sure that previously 
        // we got some open parenthesis, then decrement the open parenthesis 
        // and move to the next index
        else if(s[index] == ')'){
            return open != 0 && solve(s, index + 1, open - 1);
        }
        // if current character is '*' we have three options available
        // it can be a '(' or ')' or '', and therefore we exploring all the 
        // three possibilities
        else{
            return solve(s, index + 1, open + 1) 
                || open != 0 && solve(s, index + 1, open - 1)
                || solve(s, index + 1, open);
        }
    }
public:
    bool checkValidString(string s) {
        return solve(s, 0, 0);
    }
};
```
