class Solution {
public:
    bool checkValidString(string s) {
        stack<int> st, st1;

        for (int i=0; i<s.length(); i++) {
            if (s[i] == '(' || s[i] == '*')
                st.push(s[i]);
            else
                if (st.empty())
                    return false;
                else {
                    while (!st.empty() && st.top() == '*') { 
                        // find the rightmost occurrence of a left-parenthesis
                        st1.push(st.top()); //pushing st1 on top of stack st
                        st.pop();   //and then remove it
                    }

                    if (st.empty()) // no left-parenthesis found, pop it
                        st1.pop();
                    else // pop left parenthesis
                        st.pop();

                    while (!st1.empty()) { 
                        // by the time st1 dont get empty push ele in st by taking topmost ele from st1                             and then pop it
                        st.push(st1.top());
                        st1.pop();
                    }
                }
        }

        int sc = 0; // asterisks count
        char c;

        while (!st.empty()) {
            c = st.top();
            st.pop();

            if (c=='*')
                sc++;
            else {
                if (sc>0) // there is an asterisk on the right of left-parenthesis
                    sc--;
                else // no asterisk on the right of left parenthesis
                    return false;
            }
        }

        return true;
    }
};
