class Solution {
public:
    bool checkValidString(string s) {

        stack<int> parenStack;
        stack<int> stars;
        int numstars = 0;
        for(int i = 0 ; i < s.size(); i++)
        {
            if(s[i] == '*')
            {
                stars.push(i);
            }
            else if(s[i] == '(')
                parenStack.push(i);
            else
            {
                if(parenStack.size() > 0)
                {
                    parenStack.pop();
                }
                else if(stars.size() > 0)
                {
                    stars.pop();
