        if(stars.size() < parenStack.size())
            return false;

        while(stars.size() && parenStack.size())
        {
            if(stars.top() > parenStack.top())
            {
                stars.pop();
                parenStack.pop();
            }
            else
            {
                return false;
            }

        }

        return true;
    }
};
