class Solution {
public:

    int dp[101][500];
    bool solve(string &s, int idx, int score){
            if(score < 0 || idx > s.size()) return false;
            if(idx == s.size() && score == 0){
             return true;
            }
             if(dp[idx][score] != -1) return dp[idx][score];
             bool flag = false;
            if(s[idx] == ')'){
               flag |= solve(s, idx + 1, score - 1);
            }
            else if(s[idx] == '('){
               flag |= solve(s, idx + 1, score + 1);
            }

            else{
               flag |= solve(s, idx + 1, score);
               flag |= solve(s, idx + 1, score + 1);
               flag |= solve(s, idx + 1, score - 1);
            }
        return dp[idx][score] = flag;
    }

    bool checkValidString(string s) {
           memset(dp, -1, sizeof(dp));
           return solve(s, 0, 0);      
    }
};

