class Solution {
public:
    bool checkValidString(string s) {
        int n = s.length();
        stack <char> bracket, star;

        for(int i = 0; i < n; i++)
        {
            if(s[i] == '(')bracket.push(i);
            else if(s[i] == '*')star.push(i);
            else 
            {
                if(!bracket.empty())
                    bracket.pop();
                else if(!star.empty())
                    star.pop();
                else
                    return false;
            }
        }
        if(!bracket.empty())
        {
            while(!bracket.empty() && !star.empty() && bracket.top() < star.top())
            {
                bracket.pop(); star.pop();
            }
        }
        return (bracket.empty())? true: false;

    }
};

