class Solution {
public:
    bool checkValidString(string s) {
        int n = s.length();

        //left to right: to balance unpaired ')'
        int wildCard = 0;
        int balance = 0;
        for(int i=0;i<n;i++){
            balance += (s[i]=='(') - (s[i]==')');
            wildCard += (s[i]=='*');

            if(balance == -1){
                if(wildCard == 0)
                    return false;
                else
                    wildCard--;

                balance = 0;
            }
        }

        //right to let: to balance unpaired '('
        wildCard=0;
        balance = 0;
        for(int i=n-1;i>=0;i--){
            balance += (s[i]==')') -(s[i]=='(');
            wildCard += (s[i]=='*');

            if(balance == -1){
                if(wildCard == 0)
                    return false;
                else
                    wildCard--;

                balance = 0;
            }
        }

        return true;
    }
};
