class Solution {
public:

    int dp[105][105];

    bool solve(string &s , int index , int open )
    {
        int n = s.length();
        if(open < 0) return false;
        if(index == n){
            if(open == 0) return true;
            return false;
        }
        bool ans = false;
        if(dp[index][open] != -1) return dp[index][open];
        if(s[index] == '('){
            ans = ans | solve(s , index + 1 , open + 1 );
        }
        else if(s[index] == ')'){
            ans = ans | solve(s , index + 1 , open - 1) ;
        }
        else if(s[index] == '*'){
            ans = ans | solve(s , index + 1 , open + 1 ) ;
            ans = ans | solve(s , index + 1 , open - 1);
            ans = ans | solve(s , index + 1 , open) ;
        }
        dp[index][open] = ans;
        return ans;
    }

    bool checkValidString(string s) {
        memset(dp , -1 , sizeof dp) ;
        return solve(s , 0 , 0);
    }
};

