class Solution {
    public boolean checkValidString(String s) {
        if(s == null || s.length() == 0) return true;
        int openCount = 0;
        int starCount = 0;
        int openMatchedWithStar = 0;
        for(int i=0; i<s.length(); i++) {
            char currentChar = s.charAt(i);
            if(currentChar == ')') {
                if(openCount > 0) { // first match it against any existing open bracket
                    openCount --;
                    continue;
                }
                if(openMatchedWithStar > 0) { // next check if an open bracket was eagerly matched with a star. 
                    starCount++;              // Make the star available. 
                    openMatchedWithStar--;    // reduce the count of open brackets eagerly matched with a star
                    continue;
                }
                if(starCount > 0) { // if none above then look for a stary star appeared before to match against.
                    starCount --;
                    continue;
                }
                return false; // if none of the above then current close bracket is unbalanced.
            }
            if(currentChar == '*') {
                if(openCount > 0) { // if there are open brackets available then eagerly match the current star with that.
                    openCount--;
                    openMatchedWithStar ++;
                } else
                    starCount++;
            }
            if(currentChar == '(') 
                openCount++;
        }
        return openCount == 0; // make sure there are no left over open brackets.
    }
} 
