class Solution {
public:
    int dp[109][109];
    bool find(string &s,int i,int open)
    {
        if(open<0)
        {
            return 0;
        }
        if(i==s.size())
        {
            return open==0;
        }
        if(dp[i][open]!=-1)
        {
            return dp[i][open];
        }
        if(s[i]=='(')
        {
           dp[i][open]=find(s,i+1,open+1);
        }
        else if(s[i]==')')
        {
            dp[i][open]=find(s,i+1,open-1);
        }
        else
        {
            dp[i][open]=find(s,i+1,open-1)||find(s,i+1,open)||find(s,i+1,open+1);
        }
        return dp[i][open];
    }
    bool checkValidString(string s)
    {
        memset(dp,-1,sizeof(dp));
        return find(s,0,0)==1;
    }
};
