class Solution {
    public boolean checkValidString(String s) {
        // stack holds indices of open parens
        Stack<Integer> stack = new Stack();
        // indices holds indices of * 
        Stack<Integer> wildCards = new Stack();

        for(int i = 0; i < s.length(); i++){
            // Simply push the index of open paren (which is meaningless)
            if(s.charAt(i) == '('){
                stack.push(i);
            }

            else if(s.charAt(i) == ')'){
                // Can pop off a matching paren
                if(!stack.isEmpty()){
                    stack.pop();
                }
                // We have a wildcard we can use (that has occured before index i)
                else if(!wildCards.isEmpty()){
                    wildCards.pop();
                }
                // No wildcards or matching parens
                else{
                    return false;
                }
            }
            // Add to wildcard
            else{
                wildCards.push(i);
            }
        }

        // Not enough wildcards 
        if(stack.size() > wildCards.size()) return false;

        // This is where we need the indices to see if the wildcards come AFTER the open parens
        while(!stack.isEmpty()){
            if(stack.peek() < wildCards.peek()){
                stack.pop();
                wildCards.pop();
            }
            else return false;
        }
        return true;
    }
}
