let rangeBitwiseAnd = (M, N, ans = 0) => {
    for (let i = 31; i > 0; --i)
        if (2**i > N && M >= 2**(i - 1))
            ans |= 2**(i - 1),
              M -= 2**(i - 1),
              N -= 2**(i - 1);
    return ans;
};
```

