class Solution {
public:
    int rangeBitwiseAnd(int M, int N, int ans = 0) {
        for (unsigned int i = 1 << 31; i > 0; i >>= 1)
            if (i > N && M >= i >> 1)
                ans |= i >> 1,
                  N -= i >> 1,
                  M -= i >> 1;
        return ans;
    }
};
```

