class Solution {
public:

    int rangeBitwiseAnd(int m, int n) {
        // if m==0, then answer is 0
        if(m==0) return 0;
        // if number is same, then answer is that number
        int diff = n-m;
        if(!diff) return m;
        // if number of bits required to represent the numbers are different, then answer is zero, because in order to go from e.g 5 bits to 6 bits, we have to flip all the 5 bits to zero.
        if(int(log2(n)) != int(log2(m))) {    return 0;}
        // if number of bits are same, then difference between the numbers in binary form are the bits that we have to change
        //like if m=35 and n = 57, then diff = 22 ~ 10110 so only 5 last 5 bits will be changed, but first take AND of m and n, then change last 5 bits. 
        int k = log2(diff);
        cout<<k;
        m = m&n;
        for(int i= k; i>=0;i--){
            // here we are setting the ith bit to zero
            m = m & (~(1<<i));
        }
        return m;
    }
};

