class Solution {
public:
    int rangeBitwiseAnd(int left, int right) {

        // Idea is bitwise and of 2 numbers will be less than or equal to that number
        // When u are given numbers in range there is no need to do repetitve AND
        // if u get a less answer that is good as ultimately u will reach there anyhow.
        // so, if u are reaching end directly why take longer steps to reach there.

        int result = right;

        while(right > left) {
            result = right & (right - 1);
            right = result;
        }

        return result;
    }
};
