class Solution {
public:
    bool validPalindrome(string s) {
        int low=0,high=s.length()-1;
        // to check if a string is palindrome or not
        // if loop runs successfully then it palindrome string
        while(high>low){
            if(s[low]==s[high]){
                low++;
                high--;
            }
            else
                break;
        }
        if(low>=high)
            return true;

        // if string is not palindrome then we have to look 
        // to skip left char where loop break and check again for palindrome
        int low1=low+1,high1=high;
        while(low1<high1){
            if(s[low1]==s[high1]){
                low1++;
                high1--;
            }
            else
                break;
        }
        if(low1>=high1)
            return true;

        // now we will skip the right most where 1st loop breaks

        int low2=low,high2=high-1;
        while(low2<high2){
            if(s[low2]==s[high2]){
                low2++;
                high2--;
            }
            else
                break;
        }
        if(low2>=high2)
            return true;
        return false;
    }
};
```

