class Solution {
public:
    bool palindrome(string A, int s, int e) {
        while(s<=e) {
            if(A[s] != A[e]) {
                return false;
            }
            s++;
            e--;
        }
        return true;
    }

    bool validPalindrome(string A) {
        int n = A.size();
        int s = 0;
        int e = n-1;

        while(s<=e) {
            if(A[s] != A[e]) {
                //delete s, check palindrome from s+1 to e
                // delete e, check palindrome from s to e-1
                // following above two cases
                if(palindrome(A, s+1, e) || palindrome(A, s, e-1)) return true;

                return false; // if no any case is not true
            }

            s++;
            e--;
        }

        // If already a palindrome
        return true;
    }
};
```

