class Solution {
    func validPalindrome(_ s: String) -> Bool {
        if s.isEmpty {
            return true
        }

        var start = s.startIndex
        var end = s.index(before: s.endIndex)
        while start < end {
            if s[start] != s[end] {
                if isValid(String(s[start...s.index(before: end)])) || isValid(String(s[s.index(after: start)...end])) {
                    return true
                }
                return false
            }
            start = s.index(after: start)
            end = s.index(before: end)
        }
        return true
