class Solution {
    public boolean validPalindrome(String s) {
        if (s == null || s.isEmpty()) {
            return true;
        }

        return helper(s, 0, s.length() - 1, 1);
    }

    private boolean helper(String s, int i, int j, int k) {
        if (j <= i) {
            return true;
        }

        while (i < j) {
            if (s.charAt(i) == s.charAt(j)) {
                i++;
                j--;
            } else {
                if (k == 0) {
                    return false;
                }
                return helper(s, i + 1, j, k - 1) || helper(s, i, j - 1, k - 1);
            }
        }

        return true;
    }
}

