        return solve(s, 0, s.size() - 1, false);
    }

    bool solve(string& s, int left, int right, bool skipped){
        while(left < right){
            if(s[left] != s[right]){
                if(skipped) return false;
                return solve(s, left + 1, right, true) || solve(s, left, right - 1, true); // skip the left or right element
            }
            left++; right--;
        }
        return true;
    }
