        int count1 = 0, count2 = 0;

        while(start < end)
        {
            if(s[start] == s[end])
            {
                start++;
                end--;
            }
            else                   //for case like a b f e e b a  
            {                      //skip from start side(f) and count1++
                start++;
                count1++;
