class Solution {
public:
   /*
   Just maintain 2 pointers i.e for start and end of string
    Keep checking if they are same

    If they are Same - then just check inside and keep going till you reach the center(left==right)(if       odd string) or left>right (if even string)
    If they are NOT same : You now have 2 options
    1) Remove Left Element and Check for the Rest of String OR
    2) Remove Right Element and Check for the Rest of the string.
    If either of them dont give palindrome then its not a palindorme.
    */



    //using recursive function
    bool ispalindrome(string s,int left,int right,int count)
    {
        if(count>1)
            return false;
        while(left<=right)
        {
            if(s[left]!=s[right])
            {
                return (ispalindrome(s,left+1,right,count+1)||ispalindrome(s,left,right-1,count+1));
            }
            else
            {
                left++;
                right--;
            }
        }
        return true;

    }


    bool validPalindrome(string s) {

        int count=0;
        int left=0;
        int right=s.size()-1;
        return ispalindrome(s,left,right,count);

    }
};
```
