impl Solution {
    pub fn valid_palindrome(s: String) -> bool {
        Self::helper(&s.as_bytes(), true)
    }

    fn helper(chars: &[u8], can_skip: bool) -> bool {
        if chars.len() == 0 || chars.len() == 1 {
            return true;
        }

        let mut i = 0;
        let mut j = chars.len() - 1;
        while i <= j {
            if chars[i] == chars[j] {
                i += 1;
                j -= 1;
                continue;
            }
            if !can_skip {
                return false;
